import React from "react";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            defenders: [
                {
                    name: "Bartezaghi Davide",
                    age: 18,
                    minutesPlayed: 70,
                    goals: 0,
                    yellowCards: 0,
                    redCards: 0,
                    assists: 0,
                    photo: "url_to_bartezaghi_photo.jpg",
                    position: "Defender"
                },
                // Додайте решту футболістів за аналогією
            ],
            goalkeepers: [
                {
                    name: "Menyan Mike",
                    age: 28,
                    minutesPlayed: 1800,
                    goals: 0,
                    yellowCards: 1,
                    redCards: 0,
                    assists: 1,
                    photo: "url_to_menyan_photo.jpg",
                    position: "Goalkeeper"
                },
                // Додайте решту воротарів за аналогією
            ],
            forwards: [
                {
                    name: "Ziru Olivier",
                    age: 37,
                    minutesPlayed: 1504,
                    goals: 11,
                    yellowCards: 8,
                    redCards: 1,
                    assists: 1,
                    photo: "url_to_ziru_photo.jpg",
                    position: "Forward"
                },
                // Додайте решту нападників за аналогією
            ]
        };
    }

    render() {
        const { defenders, goalkeepers, forwards } = this.state;
        return (
            <div className="app">
                <h1>Defenders</h1>
                <div className="players-container">
                    {defenders.map((player, index) => (
                        <div className="player-card" key={index}>
                            <img src={player.photo} alt={player.name} />
                            <div className="player-info">
                                <h2>{player.name}</h2>
                                <p>{player.position}</p>
                            </div>
                        </div>
                    ))}
                </div>

                <h1>Goalkeepers</h1>
                <div className="players-container">
                    {goalkeepers.map((player, index) => (
                        <div className="player-card" key={index}>
                            <img src={player.photo} alt={player.name} />
                            <div className="player-info">
                                <h2>{player.name}</h2>
                                <p>{player.position}</p>
                            </div>
                        </div>
                    ))}
                </div>

                <h1>Forwards</h1>
                <div className="players-container">
                    {forwards.map((player, index) => (
                        <div className="player-card" key={index}>
                            <img src={player.photo} alt={player.name} />
                            <div className="player-info">
                                <h2>{player.name}</h2>
                                <p>{player.position}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        );
    }
}

function Team1() {
    return (
        <div>
            Основная команда
        </div>
    );
}

export { App, Team1 };
