// Team1.js
import React from "react";
import "./Team1.css"; // Підключаємо файл стилів

export default function Team1() {
    // Ініціалізуємо масиви футболістів
    const defenders = [
        {
            name: "Bartezaghi Davide",
            age: 18,
            minutesPlayed: 70,
            goals: 0,
            yellowCards: 0,
            redCards: 0,
            assists: 0,
            photo: "https://pictures.ua.tribuna.com/image/3106a00f-7ead-448d-b2cf-61cbb9e6317b",
            position: "Defender"
        },
        // Додайте решту футболістів за аналогією
    ];

    const goalkeepers = [
        {
            name: "Menyan Mike",
            age: 28,
            minutesPlayed: 1800,
            goals: 0,
            yellowCards: 1,
            redCards: 0,
            assists: 1,
            photo: "url_to_menyan_photo.jpg",
            position: "Goalkeeper"
        },
        // Додайте решту воротарів за аналогією
    ];

    const forwards = [
        {
            name: "Ziru Olivier",
            age: 37,
            minutesPlayed: 1504,
            goals: 11,
            yellowCards: 8,
            redCards: 1,
            assists: 1,
            photo: "url_to_ziru_photo.jpg",
            position: "Forward"
        },
        // Додайте решту нападників за аналогією
    ];

    const players = [...defenders, ...goalkeepers, ...forwards];

    return (
        <div>
            <h1>Основная команда</h1>
            <div className="players-container">
                {players.map((player, index) => (
                    <div className="player-card" key={index}>
                        <div className="player-photo-frame">
                            <img src={player.photo} alt={player.name} />
                        </div>
                        <div className="player-info">
                            <h2>{player.name}</h2>
                            <p>{player.position}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
