        {
            name: 'Sponsor 1',
            website: 'https://ua.puma.com/uk/?&utm_source=GGL&utm_medium=BS&utm_campaign=ggl_bs_np_ser_brand_exact_search_ukraine&gad_source=1&gclid=Cj0KCQiAw6yuBhDrARIsACf94RXi-XVYVrIq_hMztlVxjxBN8723QSNgaShak6_rqEJI9R0jfBBA4X4aAqWWEALw_wcB',
            image: 'https://c1.klipartz.com/pngpicture/248/505/sticker-png-nike-symbol-logo-puma-small-to-mediumsized-cats-tail-thumbnail.png'
        },
        { name: 'Sponsor 2', website: '', image: '' },
        { name: 'Sponsor 3', website: '', image: '' },
        // Додайте решту спонсорів за аналогією
    ];

    const [sponsors, setSponsors] = useState(initialSponsors);

