import React from 'react';
import Sponsor from "./components/Sponsor";

function Footer() {
    const sponsors = [
        {
            name: 'Sponsor 1',
            website: 'https://ua.puma.com/uk/',
            image: 'https://assets-eu-01.kc-usercontent.com/1293c890-579f-01b7-8480-902cca7de55e/28aa448e-19f1-4c27-8ec2-2af25c679693/Puma_Resize_2.png'
        },
        {
            name: 'Sponsor 2',
            website: 'https://example.com/sponsor2',
            image: '/images/sponsor2.png'
        },
        // Додайте інші спонсори за аналогією
    ];

    const renderSponsors = () => {
        const rows = [];
        const row1 = [];
        const otherRows = [];
        sponsors.forEach((sponsor, index) => {
            if (index < 4) {
                row1.push(
                    <Sponsor
                        key={index}
                        name={sponsor.name}
                        website={sponsor.website}
                        image={sponsor.image}
                    />
                );
            } else {
                otherRows.push(
                    <Sponsor
                        key={index}
                        name={sponsor.name}
                        website={sponsor.website}
                        image={sponsor.image}
                    />
                );
            }
        });
        rows.push(row1);
        for (let i = 0; i < otherRows.length; i += 5) {
            rows.push(otherRows.slice(i, i + 5));
        }
        return rows.map((row, index) => (
            <div key={index} className="sponsor-row">
                {row}
            </div>
        ));
    };

    return (
        <footer>
            <div className="sponsor-pyramid">
                {renderSponsors()}
            </div>
            <p className="footer-text">Всі права захищені &copy;</p>
        </footer>
    );
}

export default Footer;
