import React, { createContext, useContext, useState } from "react";
import { BrowserRouter as Router, Link, Route, Routes, useLocation } from "react-router-dom";

// Створіть контекст для зберігання поточного маршруту
const CurrentRouteContext = createContext();

function Header() {
    // Отримайте значення поточного маршруту з контексту
    const currentRoute = useContext(CurrentRouteContext);

    return (
        <header>
            <div className="logo-container">
                <img
                    src="https://www.skrill.com/fileadmin/Personal/promotions/skrill-ac-milan/ac-milan.PNG"
                    alt="AC Milan Logo"
                    className="logo-img"
                />
                <span className="logo-text">AC Milan</span>
                <ul className="nav">
                    <li><Link to="/about" className={currentRoute === "/about" ? "active" : ""}>Про клуб</Link></li>
                    <li><Link to="/team" className={currentRoute === "/team" ? "active" : ""}>Основна команда</Link></li>
                    <li><Link to="/youth" className={currentRoute === "/youth" ? "active" : ""}>Молодіжна команда</Link></li>
                </ul>
            </div>
            <div className="presentation"></div>
        </header>
    );
}

function About() {
    return <h2>Про клуб</h2>;
}

function Team() {
    return <h2>Основна команда</h2>;
}

function Youth() {
    return <h2>Молодіжна команда</h2>;
}

function App() {
    const [currentRoute, setCurrentRoute] = useState(useLocation().pathname);

    // Встановіть значення поточного маршруту в контексті
    return (
        <Router>
            <CurrentRouteContext.Provider value={currentRoute}>
                <div>
                    <Header />
                    <Routes>
                        <Route path="/about" element={<About />} />
                        <Route path="/team" element={<Team />} />
                        <Route path="/youth" element={<Youth />} />
                    </Routes>
                </div>
            </CurrentRouteContext.Provider>
        </Router>
    );
}

export default App;
