// components/Header.js
import React, { useContext } from "react";
import { NavLink } from "react-router-dom";
import { NavigationContext } from "../App";

function Header() {
    const { setActivePage } = useContext(NavigationContext);

    const handleNavigation = (page) => {
        setActivePage(page);
    };

    return (
        <header>
            <div className="logo-container">
                <img
                    src="https://www.skrill.com/fileadmin/Personal/promotions/skrill-ac-milan/ac-milan.PNG"
                    alt="AC Milan Logo"
                    className="logo-img"
                />
                <span className="logo-text">AC Milan</span>
                <ul className="nav">
                    <li><NavLink to="/" exact onClick={() => handleNavigation("home")}>Home</NavLink></li>
                    <li><NavLink to="/about" onClick={() => handleNavigation("about")}>Про клуб</NavLink></li>
                    <li><NavLink to="/team" onClick={() => handleNavigation("team")}>Основна команда</NavLink></li>
                    <li><NavLink to="/youth" onClick={() => handleNavigation("youth")}>Молодіжна команда</NavLink></li>
                </ul>
            </div>
            <div className="presentation"></div>
        </header>
    );
}

export default Header;
