// Team2Button.js
import React, { useState } from "react";
import "../index.css"; // Підключаємо файл стилів

export default function YouthTeam() {

    const [selectedPlayer, setSelectedPlayer] = useState(null);
    const youthGoalkeepers = [
        {
            name: "Bartocchioni Andrea",
            age: 19,
            minutesPlayed: 900,
            goals: 0,
            yellowCards: 0,
            redCards: 0,
            assists: 0,
            photo: "https://example.com/bartocchioni_andrea.jpg",
            position: "Goalkeeper",
            matchesPlayed: 10
        },
        {
            name: "Nava Lapo",
            age: 20,
            minutesPlayed: 90,
            goals: 0,
            yellowCards: 0,
            redCards: 0,
            assists: 0,
            photo: "https://example.com/nava_lapo.jpg",
            position: "Goalkeeper",
            matchesPlayed: 1
        },
        {
            name: "Raver Noah",
            age: 18,
            minutesPlayed: 900,
            goals: 0,
            yellowCards: 0,
            redCards: 0,
            assists: 0,
            photo: "https://example.com/raver_noah.jpg",
            position: "Goalkeeper",
            matchesPlayed: 10
        }
    ];

    const youthDefenders = [
        {
            name: "Nsiala Clinton",
            age: 20,
            minutesPlayed: 1579,
            goals: 2,
            yellowCards: 0,
            redCards: 2,
            assists: 1,
            photo: "https://example.com/nsiala_clinton.jpg",
            position: "Defender",
            matchesPlayed: 18
        },
        {
            name: "Bakun Adam",
            age: 18,
            minutesPlayed: 1303,
            goals: 0,
            yellowCards: 0,
            redCards: 6,
            assists: 1,
            photo: "https://example.com/bakun_adam.jpg",
            position: "Defender",
            matchesPlayed: 20
        },
        // Додайте інших захисників тут...
    ];

    const youthMidfielders = [
        {
            name: "Perina Joele",
            age: 18,
            minutesPlayed: 65,
            goals: 0,
            yellowCards: 0,
            redCards: 0,
            assists: 0,
            photo: "https://example.com/perina_joele.jpg",
            position: "Midfielder",
            matchesPlayed: 3
        },
        {
            name: "Bonomi Alessandro",
            age: 17,
            minutesPlayed: 849,
            goals: 4,
            yellowCards: 0,
            redCards: 1,
            assists: 0,
            photo: "https://example.com/bonomi_alessandro.jpg",
            position: "Midfielder",
            matchesPlayed: 19
        },
        // Додайте інших півзахисників тут...
    ];

    const youthForwards = [
        {
            name: "Simmelhack Alexander",
            age: 18,
            minutesPlayed: 159,
            goals: 2,
            yellowCards: 0,
            redCards: 1,
            assists: 0,
            photo: "https://example.com/simmelhack_alexander.jpg",
            position: "Forward",
            matchesPlayed: 8
        },
        {
            name: "Kamarda Francesco",
            age: 15,
            minutesPlayed: 1005,
            goals: 5,
            yellowCards: 0,
            redCards: 1,
            assists: 0,
            photo: "https://example.com/kamarda_francesco.jpg",
            position: "Forward",
            matchesPlayed: 18
        },
        // Додайте інших нападників тут...
    ];


    const coach = [
        {
            name: "Іван Іванови",
            age: 45,
            position: "Тренер",
            photo: "https://example.com/coach.jpg"
        }
    ];

    const openPlayerModal = (player) => {
        if (player.position !== "Тренер") {
            setSelectedPlayer(player);
        }
    };

    const closePlayerModal = () => {
        setSelectedPlayer(null);
    };

    const handleModalBackgroundClick = (event) => {
        if (event.target.classList.contains("modal")) {
            closePlayerModal();
        }
    };

    return (
        <div>
            <div className="team-header">
                <h1>Молодіжна команда</h1>
            </div>

            <div className="players-container" style={{ backgroundImage: 'url("https://cdn.pixabay.com/photo/2016/11/23/15/08/wooden-floor-1853417_1280.jpg")' }}>
                {/* Відображення гравців молодіжної команди */}
                {/* Тренер */}
                {coach.map((coach, index) => (
                    <div className="player-card coach" key={index} onClick={() => openPlayerModal(coach)}>
                        <div className="player-photo-frame">
                            <img src={coach.photo} alt={coach.name} />
                        </div>
                        <div className="player-info">
                            <h2>{coach.name}</h2>
                            <p>{coach.position}</p>
                        </div>
                    </div>
                ))}
                {/* Молодіжні воротарі */}
                {youthGoalkeepers.map((player, index) => (
                    <div className="player-card goalkeeper" key={index} onClick={() => openPlayerModal(player)}>
                        <div className="player-photo-frame">
                            <img src={player.photo} alt={player.name} />
                        </div>
                        <div className="player-info">
                            <h2>{player.name.split(' ')[0]}</h2>
                            <h2>{player.name.split(' ')[1]}</h2>
                            <p>{player.position}</p>
                        </div>
                    </div>
                ))}
                {/* Молодіжні захисники */}
                {youthDefenders.map((player, index) => (
                    <div className="player-card defender" key={index} onClick={() => openPlayerModal(player)}>
                        <div className="player-photo-frame">
                            <img src={player.photo} alt={player.name} />
                        </div>
                        <div className="player-info">
                            <h2>{player.name.split(' ')[0]}</h2>
                            <h2>{player.name.split(' ')[1]}</h2>
                            <p>{player.position}</p>
                        </div>
                    </div>
                ))}
                {/* Молодіжні півзахисники */}
                {youthMidfielders.map((player, index) => (
                    <div className="player-card midfielder" key={index} onClick={() => openPlayerModal(player)}>
                        <div className="player-photo-frame">
                            <img src={player.photo} alt={player.name} />
                        </div>
                        <div className="player-info">
                            <h2>{player.name.split(' ')[0]}</h2>
                            <h2>{player.name.split(' ')[1]}</h2>
                            <p>{player.position}</p>
                        </div>
                    </div>
                ))}
                {/* Молодіжні нападники */}
                {youthForwards.map((player, index) => (
                    <div className="player-card forward" key={index} onClick={() => openPlayerModal(player)}>
                        <div className="player-photo-frame">
                            <img src={player.photo} alt={player.name} />
                        </div>
                        <div className="player-info">
                            <h2>{player.name.split(' ')[0]}</h2>
                            <h2>{player.name.split(' ')[1]}</h2>
                            <p>{player.position}</p>
                        </div>
                    </div>
                ))}
                {selectedPlayer && (
                    <div className="modal" onClick={handleModalBackgroundClick}>
                        <div className="modal-content">
                            <span className="close" onClick={closePlayerModal}>&times;</span>
                            <div className="player-photo-frame">
                                <img src={selectedPlayer.photo} alt={selectedPlayer.name} />
                            </div>
                            <div className="player-info">
                                <h2>{selectedPlayer.name}</h2>
                                <p>Age: {selectedPlayer.age}</p>
                                <p>Matches Played: {selectedPlayer.matchesPlayed}</p>
                                <p>Minutes Played: {selectedPlayer.minutesPlayed}</p>
                                <p>Goals: {selectedPlayer.goals}</p>
                                <p>Assists: {selectedPlayer.assists}</p>
                                <p>Yellow Cards: {selectedPlayer.yellowCards}</p>
                                <p>Red Cards: {selectedPlayer.redCards}</p>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
